import React, { Component } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  FlatList,
} from "react-native";
import { FontAwesome } from "@expo/vector-icons";
import Item from './components/Item';
import skillData from "./skillData.json";

export default class App extends Component {
  render() {
    return (
      <View style={styles.container}>
        <View style={styles.profil}>
          <View style={styles.profil_box}>
            <Image
              source={require("./assets/profil.jpg")}
              style={styles.profil_img}
            />
            <View style={styles.profil_text}>
              <Text
                style={{
                  marginBottom: 3,
                  fontSize: 14,
                  textAlign: "center",
                  color: "#476B62",
                }}
              >
                SELAMAT PAGI
              </Text>
              <Text
                style={{
                  fontSize: 30,
                  textAlign: "center",
                  color: "black",
                }}
              >
                Username
              </Text>
              <Text
                style={{
                  fontSize: 16,
                  textAlign: "center",
                  color: "#476B62",
                }}
              >
                username@mail.com
              </Text>
              <TouchableOpacity style={styles.tombol_keluar}>
                <Text
                  style={{
                    fontSize: 16,
                    textAlign: "center",

                    color: "white",
                  }}
                >
                  KELUAR
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
        <View style={{ flex: 1 }}>
          <FlatList
            data={skillData.items}
            renderItem={(skill) => <Item skill={skill.item} />}
            keyExtractor={(items) => items.key}
          />
        </View>
        <View style={styles.menu}>
          <TouchableOpacity>
            <FontAwesome
              name="home"
              size={30}
              color="#476B62"
              style={{ margin: 40 }}
            />
          </TouchableOpacity>
          <TouchableOpacity>
            <FontAwesome
              name="plus-square-o"
              size={30}
              color="#476B62"
              style={{ margin: 40 }}
            />
          </TouchableOpacity>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#7AD1D1",
    flex: 1,
  },
  profil: {
    paddingTop: 200 + 33,
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column",
  },
  profil_box: {
    backgroundColor: "white",
    position: "absolute",
    width: 298,
    height: 147,
    borderRadius: 50,
    flexDirection: "row",
    alignItems: "center",
    elevation: 6,
  },
  profil_img: {
    width: 93,
    height: 93,
    borderRadius: 100,
    margin: 20,
  },
  profil_text: {
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  tombol_keluar: {
    backgroundColor: "#476B62",
    marginTop: 5,
    width: 113,
    height: 29,
    borderRadius: 50,
    elevation: 6,
    justifyContent: "center",
  },
  box_judul: {
    backgroundColor: "white",
    borderTopLeftRadius: 50,
    borderTopRightRadius: 50,
    width: 298,
    height: 71,
    justifyContent: "center",
    elevation: 6,
  },
  menu: {
    backgroundColor: "white",
    height: 59,
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
    flexDirection: "row",
  },
});
