import React from 'react';
import { View, Text, StyleSheet, FlatList, Image } from 'react-native';

const BASE_URL = 'https://www.googleapis.com/books/v1/volumes?q=quilting';

class TopRatingScreen extends React.Component {
    constructor(){
        super();
        this.state = {
        newsData: {}
        };
    }

    componentDidMount() {
        this.getAllNews();
    }

    getAllNews() {
        fetch(BASE_URL)
            .then((response) => response.json())
            .then((json) => this.setState({
                newsData: json
            })
            )
            .catch((error) => console.log(error));
    }

    render() {
        const {newsData} = this.state;

        
        return (
            <View style={styles.container}>
                <Text style={styles.judul}>Top Rating Novels</Text>
                <FlatList
                    data = {booksData.items}
                    style = {styles.buku}
                    keyExtractor = {(item, index) => (`${item}--${index}`)}

                    renderItem = {({item}) => (
                        <View>
                            <Image style = {styles.tinyLogo} source={{uri: item.volumeinfo.imageLinks.smallThumbnail}}/>
                            <Text>{item.volumeinfo.title}</Text>
                        </View>
                    )
                    }
                />
            </View>
        );
    }

}

const styles = StyleSheet.create({
    buku : {
        width : '100%',
        height : 50,
        backgroundColor : 'white',
        borderRadius: 10,
    },
    container : {
        paddingTop: 70,
        paddingHorizontal: 20,
        backgroundColor : '#edf0f5',
        flex: 1
    },
    judul : {
        fontSize: 30
    },
    tinyLogo: {
        width: 60,
        height: 60,
        marginRight: 5,
        flex: 0
    },
})

export default TopRatingScreen;
