import React from 'react';
import { View, Text, StyleSheet, StatusBar, ListItem } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';

// import Item from '../components/Item';
import BooksData from '../booksData.json';

function TopNovelsScreen(props) {
    return (
        <View style={styles.container}>
            <StatusBar backgroundColor={'#edf0f5'}/>
            <Text style={styles.judul}>Discover Book</Text>
            <View style={{ flex : 1 }}>
                <FlatList
                    data={BooksData.items}
                    // keyExtractor={(book) => book.key}
                    renderItem={({book}) => (
                        <ListItem title={book.title}/>
                    )}
                />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    buku : {
        width : '100%',
        height : 50,
        backgroundColor : 'white',
        borderRadius: 10,
    },
    container : {
        paddingTop: 70,
        paddingHorizontal: 20,
        backgroundColor : '#edf0f5',
        flex: 1
    },
    judul : {
        fontSize: 30
    },
})

export default TopNovelsScreen;
