import React, { Component } from "react";
import {
  Platform,
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
} from "react-native";
import Icon from "react-native-vector-icons/MaterialCommunityIcons";

export default class Item extends Component {
  render() {
    let skill = this.props.skill;
    return (
      <View style={styles.body}>
        <View>
          <View style={styles.box_judul}>
            <Text style={styles.teks_judul}>{skill.categoryName}</Text>
          </View>
        </View>
        <View style={styles.box_ket}>
          <Icon
            name={skill.iconName}
            size={60}
            color="#38574F"
            style={{ marginHorizontal: 35, marginVertical: 10 }}
          ></Icon>
          <View style={{ flexDirection: "column", justifyContent: "center" }}>
            <Text style={{ fontSize: 18 }}>{skill.skillName}</Text>
            <Text style={{ marginTop: 2, fontSize: 16, color: "#38574F" }}>
              {skill.tingkatan} - {skill.percentageProgress}{" "}
            </Text>
          </View>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  body: {
    flex: 1,
    paddingHorizontal: 50,
    marginBottom: 20,
    alignItems: "baseline",
  },
  box_judul: {
    backgroundColor: "#FFD861",
    borderTopRightRadius: 50,
    borderTopLeftRadius: 50,
    justifyContent: "center",
    height: 25,
    elevation: 6,
  },
  teks_judul: {
    color: "#38574F",
    fontSize: 12,
    fontWeight: "bold",
    margin: 20,
  },
  box_ket: {
    flexDirection: "row",
    backgroundColor: "white",
    width: 311,
    borderBottomRightRadius: 50,
    borderBottomLeftRadius: 50,
    borderTopRightRadius: 50,
    elevation: 6,
  },
});
